'use client';
import { cva } from 'class-variance-authority';
import RCImage from 'rc-image';
import { ReactElement, SyntheticEvent } from 'react';

import { cn } from '../../lib/utils';

const imageVariants = cva('', {
  variants: {
    rounded: {
      none: 'rounded-none',
      sm: 'rounded-sm',
      default: 'rounded',
      md: 'rounded-md',
      lg: 'rounded-lg',
      xl: 'rounded-xl',
      '2xl': 'rounded-2xl',
      '3xl': 'rounded-3xl',
      full: 'rounded-full',
    },
    object: {
      contain: 'object-contain',
      cover: 'object-cover',
      fill: 'object-fill',
      none: 'object-none',
    },
  },
  defaultVariants: {
    rounded: 'none',
  },
});

export interface ImageProps {
  /**
   * 图片地址
   */
  src: string;
  /**
   * 图像宽度
   * string | number
   */
  width?: string | number;
  /**
   * 图像高度
   * string | number
   */
  height?: string | number;
  /**
   * 加载占位，为 true 时使用默认占位
   */
  placeholder?: boolean | ReactElement;
  /**
   * 加载失败容错地址
   */
  fallback?: string;
  /**
   * 预览参数，为 false 时禁用
   */
  // preview?: boolean;
  /**
   * 圆角，默认为 none
   */
  rounded?:
    | 'none'
    | 'sm'
    | 'default'
    | 'md'
    | 'lg'
    | 'xl'
    | '2xl'
    | '3xl'
    | 'full';
  /**
   * 填充类型
   */
  object?: 'contain' | 'cover' | 'fill' | 'none';
  /**
   * 图像描述
   */
  alt?: string;
  /**
   * image样式覆盖
   */
  className?: string;
  /**
   * 是否是背景图
   */
  isBackgroud?: boolean;
  /**
   * 加载错误回调
   */
  onError?: (event: SyntheticEvent<HTMLImageElement, Event>) => void;
}

export function Image({
  className,
  placeholder = true,
  width = '100%',
  height = '100%',
  src,
  rounded = 'none',
  fallback = '',
  object = 'cover',
  isBackgroud = false,
  ...otherProps
}: ImageProps) {
  return (
    <RCImage
      placeholder={placeholder}
      width={width}
      height={height}
      src={src}
      fallback={
        fallback ||
        'data:image/png;base64,'
      }
      preview={false}
      rootClassName={cn(
        imageVariants({ rounded, object }),
        isBackgroud && 'absolute top-0 left-0 z-0 w-full h-full object-cover',
        className,
      )}
      {...otherProps}
    />
  );
}
